def celsiusToFahrenheit(degrees):

    """
    Converts input Celsius degrees to Fahrenheit
    """

    return (degrees * 9 / 5) + 32

def fahrenheitToCelsius(degrees):

    """
    Converts input Fahrenheit degrees to Celsius
    """

    return (degrees - 32) * 5 / 9

def celsiusToKelvin(degrees):

    """
    Converts input Celsius degrees to Kelvin
    """

    return degrees - 273

def kelvinToCelsius(degrees):

    """
    Converts input Kelvin degrees to Celsius
    """

    return degrees + 273

def calorieToJoule(calories):

    """
    Converts input Calories to Joules
    """

    return calories * 4.184

def jouleToCalorie(joules):

    """
    Converts input Joules to Calories
    """

    return joules * 0.239006

def meterToFeet(meter):

    """
    Converts input Meter to Feet
    """

    return meter * 3.2808399

def feetToMeter(feet):

    """
    Covnerts input Feet to Meter
    """
    
    return feet * 0.3048

def kiloToPound(kilo):

    """
    Converts input Kilo to Pound
    """

    return kilo * 2.20462262

def poundtoKilo(pound):

    """
    Converts input Pound to Kilo
    """

    return pound * 0.45359237